goodWords.get = function () {
    var word = goodWords[Math.floor(Math.random() * goodWords.length)];
    var sidx = -1;
    var si1 = word.indexOf("(");
    var si2 = word.indexOf("<");
    if (si1 > -1 && si2 > -1) {
        sidx = Math.min(word.indexOf("("), word.indexOf("<"));
    }
    else if (si1 > -1) {
        sidx = si1;
    }
    else if (si2 > -1) {
        sidx = si2;
    }

    if (sidx != -1) {
        word = word.substr(0, sidx) + "<br/><b>" + word.substr(sidx).replace("<", "&lt;") + "</b>";
    }
    else {
        word = word.replace("<", "&lt;");
    }
    return word;
};